import React from "react";

import "./style.css";

import { ReactComponent as QuotesSVG } from "../../assets/quotes.svg";

export default class Qoutes extends React.Component {
  componentDidUpdate(prevProps) {
    if (prevProps.quote !== this.props.quote) {
      this.setState({
        loading: false,
      });
    }
  }
  render() {
    const { author, quote, color } = this.props;
    return (
      <div className="quotes">
        {this.state.loading ? (
          <p>loading</p>
        ) : (
          <>
            <div className="quote">
              <QuotesSVG fill={color} width={30} height={30} />
              <p className="quote-text" style={{ color: color }}>
                {quote}
              </p>
            </div>
            <p className="author" style={{ color: color }}>
              {author}
            </p>
          </>
        )}
      </div>
    );
  }
}
