import React from 'react';

import './style.css';

export default class Button extends React.Component {
  render() {
    const { children, style,link,quote,randomQuotes,color} = this.props;
    return (
      <button
        style={{
          backgroundColor: color,
          ...style,
        }}
        className='button'
        onClick={(e) => {
          if(link)window.open(link, "target");
          else randomQuotes();

        }}
      >
        {children}
      </button>
    );
  }
}
