import React from 'react';

import './App.css';

import Quotes from './components/Quotes';
import Actions from './components/Actions';

import quotesData from './data/quotes.json';

const colors = [
  '#16a085',
  '#27ae60',
  '#2c3e50',
  '#f39c12',
  '#e74c3c',
  '#9b59b6',
  '#FB6964',
  '#342224',
  '#472E32',
  '#BDBB99',
  '#77B1A9',
  '#73A857',
];
const generateRandom = () => {
  const randonQuoteIndex = Math.floor(Math.random() * quotesData.quotes.length);
  return quotesData.quotes[randonQuoteIndex];
};
const genereateRandomColor = () =>{
  const index = Math.floor(Math.random()*colors.length);
  return colors[index];
};
class App extends React.Component {

  state= {
    quote: {},
    color: genereateRandomColor(),
    number:19
  }
  componentDidMount(){
    // if(age==='yes'){
    //   console.log('did mount ')
    // }

      this.timerId =setInterval(this.randomQuotes,5000)
  }
  componentWillUnmount(){
    clearInterval(this.timerId);
  }

  randomQuotes=()=>{
    this.setState({
      quote: generateRandom(),
      color: genereateRandomColor(),
    });
  }
  render() {
    console.log('render');
    return (
      <div
        style={{
          backgroundColor: this.state.color,
        }}
        className='App'
      >
        <div className='quotes-container'>
          <Quotes 
          author = {this.state.quote.author}
          quote = {this.state.quote.quote}
          color = {this.state.color}/>
          <hr />
          <Actions  randomQuotes={this.randomQuotes}
          color = {this.state.color}/>
        </div>
      </div>
    );
  }
}

export default App;
