import React from 'react';

import './style.css';

import { ReactComponent as TwitterSVG } from '../../assets/twitter.svg';
import { ReactComponent as TumblrSVG } from '../../assets/tumblr.svg';

import Button from '../../UI/Button';

export default class Actions extends React.Component {
  render() {
    const {color,randomQuotes} = this.props;
    return (
      <div className='actions'>
        <div className='links'>
          <Button color={color} style={{ marginRight: '1rem' }} link = "https://twitter.com/?lang=ru">
            <TwitterSVG fill={'white'} width={10} height={10} />
          </Button>
          <Button color={color} link="https://www.tumblr.com/">
            <TumblrSVG fill={'white'} width={10} height={10} />
          </Button>
        </div>

        <Button
          color={color}
          randomQuotes={randomQuotes}

        >
          New Quote
        </Button>
      </div>
    );
  }
}
